import React, { useState, useEffect, useRef } from 'react';
import './Results.css';


function Results(){
    const [percentFaculty, setPercentFaculty] = useState(0.9283);

    // const [percentFaculty, setPercentFaculty] = useState(parseFloat(localStorage.getItem('PercentFaculty')).toFixed(2) || 0.68);
    const [percentStudent, setPercentStudent] = useState(parseFloat(localStorage.getItem('PercentStudent')).toFixed(2) || 0.83);
    const [probFaculty5, setProbFaculty5] = useState(parseFloat(localStorage.getItem('ProbFaculty5')).toFixed(2) || 0.01);
    const [probFaculty25, setProbFaculty25] = useState(parseFloat(localStorage.getItem('ProbFaculty25')).toFixed(2) || 0.06);
    const [probFaculty50, setProbFaculty50] = useState(parseFloat(localStorage.getItem('ProbFaculty50')).toFixed(2) || 0.18);
    const [probFaculty75, setProbFaculty75] = useState(parseFloat(localStorage.getItem('ProbFaculty75')).toFixed(2) || 0.54);
    const [probFaculty95, setProbFaculty95] = useState(parseFloat(localStorage.getItem('ProbFaculty95')).toFixed(2) || 2.81);
    const [probStudent5, setProbStudent5] = useState(parseFloat(localStorage.getItem('ProbStudent5')).toFixed(2) || 0.04);
    const [probStudent25, setProbStudent25] = useState(parseFloat(localStorage.getItem('ProbStudent25')).toFixed(2) || 0.14);
